    private void btnLoginActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnLoginActionPerformed
        // TODO add your handling code here:
        if(validInput()){
            try
        {
            Student s;
            s= new Student(Integer.parseInt(txtRN.getText()),txtSN.getText(),
                        Double.parseDouble(txtSG.getText()),txtSE.getText(),
                    txtPN.getText(),txtADR.getText());
            sms.addStudent(s);
            
            Formatter f = new Formatter("d:\\SMS\\students.txt");
            
            for(Student st:sms.getStudentList()){
                f.format("%d,%s,%.2f,%s,%s,%s\r\n", 
                    st.getRollnumber(),st.getName(),
                    st.getGrade(),st.getEmail(),
                    st.getPhone(),st.getAdr());
            }
            
            f.close();   
            id++;
            txtRN.setText(String.format("%d", id));
            txtSN.requestFocus();
            lblInfo.setText("Student added with succes.");
            txtSN.setText("");
            txtPN.setText("");
            txtSE.setText("");
            txtSG.setText("");
            txtADR.setText("");
        }
        catch(Exception Ex){
            JOptionPane.showMessageDialog(null,"*File Error!* :"+Ex.getLocalizedMessage());
        }
        }
        

    }//GEN-LAST:event_btnLoginActionPerformed

