    private void formWindowOpened(java.awt.event.WindowEvent evt) {//GEN-FIRST:event_formWindowOpened
        // TODO add your handling code here:
        sms = new StudentManagementSystem();
        
        id= sms.getStudentList().get(sms.getStudentList().size()-1).getRollnumber();
        
        sms.displayAllStudent();
           
        id++;
        
        txtRN.setText(String.format("%d", id));
        txtSN.requestFocus();
    }//GEN-LAST:event_formWindowOpened
    
    public boolean validInput(){
        
        if(txtSN.getText().isBlank()|| !txtSN.getText().matches("[a-zA-Z]{3,}"))
        {
            this.errName.setText("Invalid Name");
            return false;
        }
        this.errName.setText("");
        if(txtPN.getText().isBlank()|| !txtPN.getText().matches("\\d{5,}"))
        {
            errPhone.setText("Invalid Phone");
            return false;
        }
        errPhone.setText("");
        if(txtSE.getText().isBlank()|| !txtSE.getText().matches("\\b[A-Za-z0-9._%-]+@[A-Za-z0-9.-]+\\.[A-Za-z]{2,4}\\b"))
        {
            this.errEmail.setText("Invalid Email");
            return false;
        }
        errEmail.setText("");
        if(txtSG.getText().isBlank()|| !txtSG.getText().matches("\\d+"))
        {
            this.errGrade.setText("Invalid Grade");
            return false;
        }
        errGrade.setText("");
        if(txtADR.getText().isBlank()|| !txtADR.getText().matches("[a-zA-Z]{3,}"))
        {
            this.errAdr.setText("Invalid Address");
            return false;
        }
        errAdr.setText("");
        return true;
    }
    
