    private void btnUpdateActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnUpdateActionPerformed
        // TODO add your handling code here:
        if(validInput()){
                
            btnUpdate.setEnabled(false);
            sms.removeStudent(std.getRollnumber());
            std.setName(txtSN.getText());
            std.setGrade(Double.parseDouble(txtSG.getText()));
            std.setEmail(txtSE.getText());
            std.setPhone(txtPN.getText());
            std.setAdr(txtADR.getText());
            sms.addStudent(std);

            Formatter f;
            try {
                f = new Formatter("d:\\SMS\\students.txt");
                 for(Student st:sms.getStudentList()){
                    f.format("%d,%s,%.2f,%s,%s,%s\r\n", 
                        st.getRollnumber(),st.getName(),
                        st.getGrade(),st.getEmail(),
                        st.getPhone(),st.getAdr());
                }

                f.close(); 
            } catch (FileNotFoundException ex) {
                 JOptionPane.showMessageDialog(null,"*File Error!* :"+ex.getLocalizedMessage());
            }
            this.lblInfo.setText("Student Updated.");
            txtSN.setText("");
            txtPN.setText("");
            txtSE.setText("");
            txtSG.setText("");
            txtADR.setText("");
            txtRN.setText(""); 
            txtRN.requestFocus();
        }
            
           
    }//GEN-LAST:event_btnUpdateActionPerformed

