    public boolean validInput(){
        
        if(txtSN.getText().isBlank()|| !txtSN.getText().matches("[a-zA-Z]{3,}"))
        {
            this.errName.setText("Invalid Name");
            return false;
        }
        this.errName.setText("");
        if(txtPN.getText().isBlank()|| !txtPN.getText().matches("\\d{5,}"))
        {
            errPhone.setText("Invalid Phone");
            return false;
        }
        errPhone.setText("");
        if(txtSE.getText().isBlank()|| !txtSE.getText().matches("\\b[A-Z0-9._%-]+@[A-Z0-9.-]+\\.[A-Z]{2,4}\\b"))
        {
            this.errEmail.setText("Invalid Email");
            return false;
        }
        errEmail.setText("");
        if(txtSG.getText().isBlank()|| !txtSG.getText().matches("\\d+"))
        {
            this.errGrade.setText("Invalid Grade");
            return false;
        }
        errGrade.setText("");
        if(txtADR.getText().isBlank()|| !txtADR.getText().matches("[a-zA-Z]{3,}"))
        {
            this.errAdr.setText("Invalid Address");
            return false;
        }
        errAdr.setText("");
        return true;
    }
    
